import React, { useState, useEffect } from "react";
import useAuth from "../SpotifyAuth/UseAuth";
import axios from "axios";
import "./Homepage.css";
import PlaylistCard from "./PlaylistCard";

function Homepage() {
  const options = {
    method: "GET",
    url: "https://spotify23.p.rapidapi.com/search/",
    params: { q: "vibes", type: "playlists", offset: "0", limit: "9" },
    headers: {
      "X-RapidAPI-Key": "c7fce06954msh082b0f51aeace99p1d1ff2jsn8ad3bcc40263",
      "X-RapidAPI-Host": "spotify23.p.rapidapi.com",
    },
  };

  const getPlaylist = () => {
    axios
      .request(options)
      .then(function (response) {
        console.log(response.data.playlists.items.data);
      })
      .catch(function (error) {
        console.error(error);
      });
  };

  useEffect(() => {
    getPlaylist();
  }, []);
  const playlist = [
    "song",
    "song",
    "song",
    "song",
    "song",
    "song",
    "song",
    "song",
    "song",
  ];

  return (
    <div className="homepage">
      <h1 className="home-title">Lets get a Grove on!</h1>
      <div className="glist-container">
        <div className="glist">
          {playlist.map((item, i) => {
            return (
              <div key={i}>
                <PlaylistCard />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default Homepage;
