import { createSlice } from "@reduxjs/toolkit";

const authSlice = createSlice({
  name: "auth",
  initialState: "",
  reducers: {
    setAuthToken: (state, action) => {
      state: action.payload;
    },
  },
});

export const { setAuthToken } = authSlice.actions;
export const authReducer = authSlice.reducer;
