import React, { useState } from "react";
import { useSignIn } from "react-auth-kit";
import "./Register.css";
import axios from "axios";
function Register() {
  const [triggerSignup, setTriggerSignup] = useState(true);
  const signIn = useSignIn();

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const validateForm = () => {
    if (
      username.trim() === "" ||
      email.trim() === "" ||
      password.trim() === ""
    ) {
      alert("Please fill in all fields");
      return false;
    }

    if (!email.includes("@")) {
      alert("Please enter a valid email address");
      return false;
    }

    return true;
  };

  const submitHandler = (e) => {
    e.preventDefault();
    if (!validateForm()) {
      return;
    }

    const body = {
      username,
      email,
      password,
    };
    const URL = "http://localhost:4838";

    axios
      .post(triggerSignup ? `${URL}/login` : `${URL}/register`, body)
      .then((res) => {
        console.log(res.data);

        signIn({
          token: res.data.token,
          tokenType: "Bearer",
          authState: {
            email: res.data.email,
          },
        });
      });

    console.log(body);
  };

  return (
    <div className="main-form-containter">
      <div className="form-containter">
        <form action="" className="form" onSubmit={submitHandler}>
          <input
            type="text"
            placeholder="Username"
            onChange={(e) => setUsername(e.target.value)}
            value={username}
          />
          <input
            type="text"
            placeholder="Email"
            onChange={(e) => setEmail(e.target.value)}
            value={email}
          />
          <input
            type="password"
            placeholder="Password"
            onChange={(e) => setPassword(e.target.value)}
            value={password}
          />
          <button className="login-btn">
            {triggerSignup ? `login` : "register"}
          </button>
        </form>
        <button
          className="signin-btn"
          onClick={() => setTriggerSignup(!triggerSignup)}
        >
          {!triggerSignup ? "need to login?" : "need to register?"}
        </button>
      </div>
    </div>
  );
}

export default Register;
