import React, { useState } from "react";
import "./Groovelist.css";
import Cookies from "js-cookie";

function Groovelist({ songs }) {
  // const [groovelistCount, setGrooveListCount] = useState(0);
  let groovelistCount = 1;
  let authState = Cookies.get("_auth_state");
  let newState = JSON.parse(authState);
  console.log(songs);

  const deleteSong = (e) => {
    songs = songs.filter((song) => {
      return song !== e.target.innerHTML;
    });
  };

  const results = songs.map((song, i) => {
    return (
      <p>
        {groovelistCount++}. {song}
        <button onClick={deleteSong}>X</button>
      </p>
    );
  });

  return (
    <div className="grovelist-main">
      {songs.length !== 0 ? (
        <div className="groovelist-container">
          <div>
            <h3>{newState.name}'s Groovelist</h3>
          </div>
          {results}
        </div>
      ) : null}
    </div>
  );
}

export default Groovelist;
