import React, { useState, useEffect, useRef } from "react";
import "./SearchBar.css";
import Grovelist from "./Grovelist";
import axios from "axios";

function SearchBar() {
  const [searchInput, setSearchInput] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [searchDropdown, setSearchDropdown] = useState(true);

  const options = {
    method: "GET",
    url: "https://spotify23.p.rapidapi.com/search/",
    params: {
      q: searchInput,
      type: "tracks",
      offset: "0",
      limit: "10",
      numberOfTopResults: "5",
    },
    headers: {
      "X-RapidAPI-Key": "e09bb0cef0mshdc6e1597af3167cp10fd05jsn41f2cd9f8081",
      "X-RapidAPI-Host": "spotify23.p.rapidapi.com",
    },
  };

  const submitSearchHandler = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.request(options);
      setSearchResults(res.data.tracks.items);
      console.log(searchResults);
    } catch (error) {
      console.error(error);
    }
  };
  let dropDown = useRef();
  useEffect(() => {
    const close = (e) => {
      if (!dropDown.current.contains(e.target)) {
        setSearchDropdown(false);
      }
    };
    document.addEventListener("mousedown", close);
  });

  // const searchRes = searchResults.map((songs, i) => {
  //   return (
  //     <p className="result-content" key={i}>
  //       {songs.tracks.items.data.name} - artist name{" "}
  //       <img src={songs.img} alt="" className="Search-cover-art" />
  //     </p>
  //   );
  // });
  return (
    <div>
      <form className="searchbar-container" onSubmit={submitSearchHandler}>
        <input
          className="search-bar"
          type="text"
          placeholder="Search..."
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
        />
        <button className="search-btn">Search</button>
      </form>
      {searchDropdown && searchResults.length > 2 ? (
        <div className="result-container" ref={dropDown}>
          {searchResults.map((songs, i) => {
            return (
              <p className="result-content" key={i}>
                {songs.data.name} - {songs.data.artists.items[0].profile.name}{" "}
                <img
                  src={songs.data.albumOfTrack.coverArt.sources[0].url}
                  alt=""
                  className="Search-cover-art"
                />
              </p>
            );
          })}
        </div>
      ) : null}
    </div>
  );
}

export default SearchBar;
