import React from "react";
import axios from "axios";

function Login() {
  const clientId = "fef107e80e32494c8a94b6022f8145dc";
  const redirectUri = "http://localhost:3000/home";
  const scopes = "user-read-private";

  const authEndpoint = "https://accounts.spotify.com/authorize";
  const authUrl = `${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes}&response_type=code`;

  return (
    <div>
      <div>Lets get you started </div>
      <button style={{ width: "40vw" }}>
        <a href={authUrl}>Authorize with Spotify</a>
      </button>
    </div>
  );
}

export default Login;
