      img: "https://cdn-p.smehost.net/sites/7f9737f2506941499994d771a29ad47a/wp-content/uploads/2020/01/spotify-logo.jpg",
    },
    {
      name: "new song",
      img: "https://cdn-p.smehost.net/sites/7f9737f2506941499994d771a29ad47a/wp-content/uploads/2020/01/spotify-logo.jpg",
    },
    {
      name: "new song",
      img: "https://cdn-p.smehost.net/sites/7f9737f2506941499994d771a29ad47a/wp-content/uploads/2020/01/spotify-logo.jpg",
    },
  ];
  // let searchRes = null;

  // if (results.length >= 1) {
  //   searchRes = <div className="search-container"></div>;
  // } else {
  //   searchRes = <div> it went wrong </div>;
  // }

  return (
    <div>
      <form className="searchbar-container">
        <input
          className="search-bar"
          type="text"
          placeholder="Search..."
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
        />
        <button className="search-btn">Search</button>
        {results.length > 1 &&
          results.map((songs, i) => {
            return (
              <div key={i} className="search-container">
                <ul>
                  {" "}
                  <li>{songs.name}</li>
                </ul>
              </div>
            );
          })}
      </form>
    </div>
  );
}

export default SearchBar;
