    {
      name: "new song",
      img: "https://cdn-p.smehost.net/sites/7f9737f2506941499994d771a29ad47a/wp-content/uploads/2020/01/spotify-logo.jpg",
    },
    {
      name: "new song",
      img: "https://cdn-p.smehost.net/sites/7f9737f2506941499994d771a29ad47a/wp-content/uploads/2020/01/spotify-logo.jpg",
    },
    {
      name: "new song",
      img: "https://cdn-p.smehost.net/sites/7f9737f2506941499994d771a29ad47a/wp-content/uploads/2020/01/spotify-logo.jpg",
    },
  ];
  // let searchRes = null;

  // if (results.length >= 1) {
  //   searchRes = <div className="search-container"></div>;
  // } else {
  //   searchRes = <div> it went wrong </div>;
  // }

  const searchRes = results.map((songs, i) => {
    return (
      <p className="result-content" key={i}>
        {songs.name} - artist name <img src="" alt="" />
      </p>
    );
  });
  return (
    <div onClick={() => setSearchDropdown(false)}>
      <form className="searchbar-container" onSubmit={submitSearchHandler}>
        <input
          className="search-bar"
          type="text"
          placeholder="Search..."
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
        />
        <button className="search-btn">Search</button>
      </form>
      {results.length > 9 ? (
        <div className="result-container">{searchRes}</div>
      ) : null}
    </div>
  );
}

export default SearchBar;
