    {
      name: "new song",
      img: "https://cdn-p.smehost.net/sites/7f9737f2506941499994d771a29ad47a/wp-content/uploads/2020/01/spotify-logo.jpg",
    },
    {
      name: "new song",
      img: "https://cdn-p.smehost.net/sites/7f9737f2506941499994d771a29ad47a/wp-content/uploads/2020/01/spotify-logo.jpg",
    },
    {
      name: "new song",
      img: "https://cdn-p.smehost.net/sites/7f9737f2506941499994d771a29ad47a/wp-content/uploads/2020/01/spotify-logo.jpg",
    },
  ];

  return (
    <div>
      <form className="searchbar-container">
        <input
          className="search-bar"
          type="text"
          placeholder="Search..."
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
        />
        <button className="search-btn">Search</button>
      </form>
      {results.length > 1
        ? results.map((song, i) => {
            <div key={i} className="search-container">
              <ul>
                {" "}
                <li>{song.name}</li>
              </ul>
            </div>;
          })
        : null}
    </div>
  );
}

export default SearchBar;
