import React, { useState } from "react";
import axios from "axios";
function CreateCover() {
  const [fileInputState, setFileInputChange] = useState("");
  const [selectedFile, setSelectedFile] = useState("");
  const [previewSource, setPreviewSource] = useState("");
  const [title, setTitle] = useState("");
  const handleFileInputChange = (e) => {
    const file = e.target.files[0];
    previewFile(file);
  };

  const previewFile = (file) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onloadend = () => {
      setPreviewSource(reader.result);
    };
  };
  const handleSubmitFile = (e) => {
    e.preventDefault();
    if (!previewFile) return;
    uploadImage(previewSource);
  };

  const uploadImage = async (base64EncodedImage) => {
    const body = {
      imgData: JSON.stringify({ data: base64EncodedImage }),
      title,
    };
    try {
      await axios.post("addCover/title", bo);
    } catch (error) {}
  };
  return (
    <div>
      <h1>Upload</h1>
      <form onSubmit={handleSubmitFile}>
        <input
          type="text"
          name="albumName"
          id=""
          value={title}
          onChange={(e) => setTitle(e.target.ariaValueText)}
        />
        <input
          type="file"
          name="image"
          onChange={handleFileInputChange}
          value={fileInputState}
          className="form-input"
        />
        <button className="cover-submit">submit</button>
      </form>
      {previewSource && (
        <img
          src={previewSource}
          alt=""
          style={{ height: "300px", width: "300px" }}
        />
      )}
    </div>
  );
}

export default CreateCover;
